import React from "react";

function HowItWorks() {
  return (
    <div className="how-it-works">
      <h2>How It Works</h2>
      <div className="how-it-works-container">
        <div className="how-it-works-box">
          <div className="how-it-works-step">Step 1</div>
          <div className="how-it-works-content">
            <h3>Search for Charitable Organizations</h3>
            <p>
              Browse through a wide range of charitable organizations and
              causes to find the ones that align with your values and interests.
            </p>
          </div>
        </div>
        <div className="how-it-works-box">
          <div className="how-it-works-step">Step 2</div>
          <div className="how-it-works-content">
            <h3>Select a Donation Amount</h3>
            <p>
              Choose the amount you wish to donate and select the payment method
              that is most convenient for you.
            </p>
          </div>
        </div>
        <div className="how-it-works-box">
          <div className="how-it-works-step">Step 3</div>
          <div className="how-it-works-content">
            <h3>Make a Difference</h3>
            <p>
              Your donation will directly support the chosen charitable
              organization and contribute to their mission of making a positive
              impact.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HowItWorks;
