import React from "react";
import Carousel from "react-bootstrap/Carousel";
import charity6 from "../img/charity6.jpeg";
import charity7 from "../img/charity7.jpeg";
import charity8 from "../img/charity8.jpeg";
import charity9 from "../img/charity9.jpeg";

function LandingPage() {
  return (
    <div className="bg-dark text-light">
      <div className="hero-section">
        <Carousel>
          <Carousel.Item>
            <img
              className="d-block w-100"
              style={{ height: "350px" }}
              src={charity6}
              alt="First slide"
            />
            <Carousel.Caption>
              <h1>Donate to Charitable Organizations</h1>
              <p>
                Help make a difference by supporting causes you care about.
              </p>
              <a href="#how-it-works" className="btn btn-primary">
                Learn More
              </a>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              style={{ height: "350px" }}
              src={charity7}
              alt="Second slide"
            />
            <Carousel.Caption>
              <h1>Empower Communities through Donations</h1>
              <p>
                Your contributions can bring positive change to those in need.
              </p>
              <a href="#how-it-works" className="btn btn-primary">
                Learn More
              </a>
            </Carousel.Caption>
          </Carousel.Item>
          {/* Add more carousel items with relevant images and captions */}
        </Carousel>
      </div>

      <div id="how-it-works" className="how-it-works-section">
        <div className="container py-5">
          <h2>How It Works</h2>
          <div className="row">
            <div className="col-md-4">
              <div className="step">
                <h3>Search for Charities</h3>
                <p>Find charitable organizations based on location or category.</p>
              </div>
            </div>
            <div className="col-md-4">
              <div className="step">
                <h3>Select a Cause</h3>
                <p>Explore different causes and projects that align with your values.</p>
              </div>
            </div>
            <div className="col-md-4">
              <div className="step">
                <h3>Make a Donation</h3>
                <p>Contribute securely and easily to your chosen charity.</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* The rest of your landing page content goes here */}

    </div>
  );
}

export default LandingPage;
